    @Override
    public boolean validate(Client client) {

        //E-mail validation
        Pattern pattern = Pattern.compile(EMAIL_PATTERN);
        if (!pattern.matcher(client.getEmail()).matches()) {
            showMessageDialog(null,new JLabel("Invalid e-mail"),"Invalid input", JOptionPane.ERROR_MESSAGE );
        }

        //Age validation
        int age = client.getAge();
        if(age < 0) {
            showMessageDialog(null,new JLabel("The age cannot be 0 or negative!"),"Invalid input", JOptionPane.ERROR_MESSAGE );
        }

        //Name validation
        String name = client.getName();
        if(name.equals("")){
            showMessageDialog(null,new JLabel("Please enter a valid name!"),"Invalid input", JOptionPane.ERROR_MESSAGE );

        }
        return true;
    }
